import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { z } from "zod";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { ObjectUploader } from "@/components/ObjectUploader";
import { Plus, Camera } from "lucide-react";
import { isUnauthorizedError } from "@/lib/authUtils";
import type { UploadResult } from "@uppy/core";

const incidentSchema = z.object({
  title: z.string().min(1, "El tÃ­tulo es requerido"),
  description: z.string().min(1, "La descripciÃ³n es requerida"),
  centerId: z.string().min(1, "El centro es requerido"),
  typeId: z.string().min(1, "El tipo es requerido"),
  priority: z.enum(["low", "medium", "high", "critical"]),
});

type IncidentFormData = z.infer<typeof incidentSchema>;

export function IncidentForm() {
  const [evidenceFiles, setEvidenceFiles] = useState<string[]>([]);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<IncidentFormData>({
    resolver: zodResolver(incidentSchema),
    defaultValues: {
      priority: "medium",
    },
  });

  // Fetch centers
  const { data: centers } = useQuery({
    queryKey: ["/api/centers"],
    retry: false,
  });

  // Fetch incident types
  const { data: incidentTypes } = useQuery({
    queryKey: ["/api/incident-types"],
    retry: false,
  });

  const createIncidentMutation = useMutation({
    mutationFn: async (data: IncidentFormData & { evidenceFiles: string[] }) => {
      const response = await apiRequest("POST", "/api/incidents", data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Ã‰xito",
        description: "Incidencia reportada correctamente",
      });
      form.reset();
      setEvidenceFiles([]);
      queryClient.invalidateQueries({ queryKey: ["/api/incidents"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error as Error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "No se pudo reportar la incidencia",
        variant: "destructive",
      });
    },
  });

  const handleGetUploadParameters = async () => {
    try {
      const response = await apiRequest("POST", "/api/objects/upload", {});
      const data = await response.json();
      return {
        method: "PUT" as const,
        url: data.uploadURL,
      };
    } catch (error) {
